import React, { useEffect, useState } from 'react';
import axios from './api';

function Dashboard() {
    const [finances, setFinances] = useState({ receitas: 0, despesas: 0 });
    const [ongs, setOngs] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [resOng, resRec, resDes] = await Promise.all([
                    axios.get('/api/ong'),
                    axios.get('/api/receita'),
                    axios.get('/api/despesa')
                ]);
                setOngs(resOng.data);

                const totalRec = resRec.data.reduce((acc, r) => acc + Number(r.valor), 0);
                const totalDes = resDes.data.reduce((acc, d) => acc + Number(d.valor), 0);
                setFinances({ receitas: totalRec, despesas: totalDes });
            } catch (err) {
                console.error(err);
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    if (loading) return <p>Carregando dados financeiros...</p>;

    return (
        <div>
            <div className="card" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1.5rem', background: 'transparent', border: 'none', padding: 0 }}>
                <div className="card" style={{ borderLeft: '4px solid var(--accent)' }}>
                    <small style={{ color: 'var(--text-muted)' }}>Total de Receitas</small>
                    <h3 style={{ fontSize: '1.8rem', color: 'var(--accent)' }}>{finances.receitas.toLocaleString()} MT</h3>
                </div>
                <div className="card" style={{ borderLeft: '4px solid var(--danger)' }}>
                    <small style={{ color: 'var(--text-muted)' }}>Total de Despesas</small>
                    <h3 style={{ fontSize: '1.8rem', color: 'var(--danger)' }}>{finances.despesas.toLocaleString()} MT</h3>
                </div>
                <div className="card" style={{ borderLeft: '4px solid var(--primary)' }}>
                    <small style={{ color: 'var(--text-muted)' }}>Saldo em Caixa</small>
                    <h3 style={{ fontSize: '1.8rem' }}>{(finances.receitas - finances.despesas).toLocaleString()} MT</h3>
                </div>
            </div>

            <div className="card">
                <h2>ONGs Parceiras</h2>
                <ul>
                    {ongs.map(ong => (
                        <li key={ong.id}>
                            <span>{ong.nome}</span>
                            <span style={{ fontSize: '0.8rem', color: 'var(--text-muted)' }}>{ong.pais} â€¢ NUIT: {ong.nuit_nif}</span>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Dashboard;
