import React, { useEffect, useState } from 'react';
import axios from './api';

function Despesas() {
    const [despesas, setDespesas] = useState([]);
    const [form, setForm] = useState({ projeto_id: '', centro_custo_id: '', categoria: '', fornecedor: '', valor: '', moeda: 'MZN', metodo_pagamento: '', comprovativo_url: '', responsavel_id: '', estado: 'submetido' });
    const [msg, setMsg] = useState('');

    useEffect(() => {
        axios.get('/api/despesa').then(res => setDespesas(res.data));
    }, []);

    const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

    const handleSubmit = async e => {
        e.preventDefault();
        try {
            await axios.post('/api/despesa', form);
            setMsg('Despesa cadastrada!');
        } catch {
            setMsg('Erro ao cadastrar.');
        }
    };

    return (
        <div className="card">
            <h2>Gestão de Despesas</h2>
            <form onSubmit={handleSubmit}>
                <input name="projeto_id" placeholder="ID do Projeto" value={form.projeto_id} onChange={handleChange} required />
                <input name="categoria" placeholder="Categoria (ex: Logística)" value={form.categoria} onChange={handleChange} />
                <input name="fornecedor" placeholder="Fornecedor" value={form.fornecedor} onChange={handleChange} />
                <input name="valor" placeholder="Valor" value={form.valor} onChange={handleChange} required />
                <select name="moeda" value={form.moeda} onChange={handleChange}>
                    <option value="MZN">Metical (MZN)</option>
                    <option value="USD">Dólar (USD)</option>
                    <option value="EUR">Euro (EUR)</option>
                </select>
                <button type="submit">Registrar Saída</button>
            </form>
            {msg && <p style={{ color: msg.includes('Erro') ? 'var(--danger)' : 'var(--accent)', marginBottom: '1rem' }}>{msg}</p>}

            <div style={{ marginTop: '2rem' }}>
                <h3 style={{ marginBottom: '1rem', color: 'var(--text-muted)' }}>Histórico Recente</h3>
                <ul>
                    {despesas.map(d => (
                        <li key={d.id}>
                            <div>
                                <strong style={{ display: 'block' }}>{d.fornecedor || 'Fornecedor N/A'}</strong>
                                <small style={{ color: 'var(--text-muted)' }}>{d.categoria}</small>
                            </div>
                            <div style={{ textAlign: 'right' }}>
                                <span style={{ fontWeight: '800', color: 'var(--text-main)', display: 'block' }}>
                                    {d.valor} {d.moeda}
                                </span>
                                <span style={{
                                    fontSize: '0.7rem',
                                    padding: '2px 8px',
                                    borderRadius: '10px',
                                    background: d.estado === 'aprovado' ? 'rgba(16,185,129,0.1)' : 'rgba(255,255,255,0.05)',
                                    color: d.estado === 'aprovado' ? 'var(--accent)' : 'var(--text-muted)'
                                }}>
                                    {d.estado.toUpperCase()}
                                </span>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}
export default Despesas;
