import React, { useEffect, useState } from 'react';
import axios from './api';

function Receitas() {
    const [receitas, setReceitas] = useState([]);
    const [form, setForm] = useState({ projeto_id: '', financiador_id: '', tipo_fundo: '', data: '', valor: '', moeda: '', taxa_cambio: '', comprovativo_url: '' });
    const [msg, setMsg] = useState('');

    useEffect(() => {
        axios.get('/api/receita').then(res => setReceitas(res.data));
    }, []);

    const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

    const handleSubmit = async e => {
        e.preventDefault();
        try {
            await axios.post('/api/receita', form);
            setMsg('Receita cadastrada!');
        } catch {
            setMsg('Erro ao cadastrar.');
        }
    };

    return (
        <div>
            <h2>Receitas</h2>
            <form onSubmit={handleSubmit}>
                <input name="projeto_id" placeholder="Projeto ID" value={form.projeto_id} onChange={handleChange} required />
                <input name="financiador_id" placeholder="Financiador ID" value={form.financiador_id} onChange={handleChange} />
                <input name="tipo_fundo" placeholder="Tipo de Fundo" value={form.tipo_fundo} onChange={handleChange} />
                <input name="data" type="date" value={form.data} onChange={handleChange} />
                <input name="valor" placeholder="Valor" value={form.valor} onChange={handleChange} required />
                <input name="moeda" placeholder="Moeda" value={form.moeda} onChange={handleChange} />
                <input name="taxa_cambio" placeholder="Taxa de CÃ¢mbio" value={form.taxa_cambio} onChange={handleChange} />
                <input name="comprovativo_url" placeholder="Comprovativo URL" value={form.comprovativo_url} onChange={handleChange} />
                <button type="submit">Cadastrar</button>
            </form>
            {msg && <p>{msg}</p>}
            <ul>
                {receitas.map(r => (
                    <li key={r.id}>{r.valor} {r.moeda} ({r.tipo_fundo})</li>
                ))}
            </ul>
        </div>
    );
}
export default Receitas;
