import React, { useState } from 'react';

import Dashboard from './Dashboard';
import Projetos from './Projetos';
import Usuarios from './Usuarios';
import Despesas from './Despesas';
import Receitas from './Receitas';
import Auditoria from './Auditoria';
import Login from './Login';
import GestaoFinanceira from './GestaoFinanceira';

function App() {
    const [token, setToken] = useState(localStorage.getItem('token'));
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));

    const handleLogin = (jwt, userData) => {
        setToken(jwt);
        setUser(userData);
        localStorage.setItem('token', jwt);
        localStorage.setItem('user', JSON.stringify(userData));
    };

    const handleLogout = () => {
        setToken(null);
        setUser(null);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
    };

    const [modulo, setModulo] = useState('dashboard');

    if (!token) {
        return <Login onLogin={handleLogin} />;
    }

    return (
        <div className="app-container">
            <header>
                <h1>CIVIS Social</h1>
                <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                    <span style={{ color: 'var(--text-muted)' }}>{user ? user.nome : 'Usuário'}</span>
                    <button className="logout-btn" onClick={handleLogout}>Sair</button>
                </div>
            </header>

            <nav style={{ flexWrap: 'wrap' }}>
                <button className={modulo === 'dashboard' ? 'active' : ''} onClick={() => setModulo('dashboard')}>Dashboard</button>
                <button className={modulo === 'financeiro' ? 'active' : ''} onClick={() => setModulo('financeiro')}>Financeiro</button>
                <button className={modulo === 'projetos' ? 'active' : ''} onClick={() => setModulo('projetos')}>Projetos</button>
                <button className={modulo === 'usuarios' ? 'active' : ''} onClick={() => setModulo('usuarios')}>Usuários</button>
                <button className={modulo === 'receitas' ? 'active' : ''} onClick={() => setModulo('receitas')}>Receitas</button>
                <button className={modulo === 'despesas' ? 'active' : ''} onClick={() => setModulo('despesas')}>Despesas</button>
                <button className={modulo === 'auditoria' ? 'active' : ''} onClick={() => setModulo('auditoria')}>Auditoria</button>
            </nav>

            <main className="main-content">
                {modulo === 'dashboard' && <Dashboard />}
                {modulo === 'financeiro' && <GestaoFinanceira />}
                {modulo === 'projetos' && <Projetos />}
                {modulo === 'usuarios' && <Usuarios />}
                {modulo === 'receitas' && <Receitas />}
                {modulo === 'despesas' && <Despesas user={user} />}
                {modulo === 'auditoria' && <Auditoria />}
            </main>
        </div>
    );
}

export default App;
