import React, { useEffect, useState } from 'react';
import axios from './api';

function Despesas() {
    const [despesas, setDespesas] = useState([]);
    const [form, setForm] = useState({ projeto_id: '', centro_custo_id: '', categoria: '', fornecedor: '', valor: '', moeda: '', metodo_pagamento: '', comprovativo_url: '', responsavel_id: '', estado: 'submetido' });
    const [msg, setMsg] = useState('');

    useEffect(() => {
        axios.get('/api/despesa').then(res => setDespesas(res.data));
    }, []);

    const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

    const handleSubmit = async e => {
        e.preventDefault();
        try {
            await axios.post('/api/despesa', form);
            setMsg('Despesa cadastrada!');
        } catch {
            setMsg('Erro ao cadastrar.');
        }
    };

    return (
        <div>
            <h2>Despesas</h2>
            <form onSubmit={handleSubmit}>
                <input name="projeto_id" placeholder="Projeto ID" value={form.projeto_id} onChange={handleChange} required />
                <input name="centro_custo_id" placeholder="Centro de Custo ID" value={form.centro_custo_id} onChange={handleChange} />
                <input name="categoria" placeholder="Categoria" value={form.categoria} onChange={handleChange} />
                <input name="fornecedor" placeholder="Fornecedor" value={form.fornecedor} onChange={handleChange} />
                <input name="valor" placeholder="Valor" value={form.valor} onChange={handleChange} required />
                <input name="moeda" placeholder="Moeda" value={form.moeda} onChange={handleChange} />
                <input name="metodo_pagamento" placeholder="Método de Pagamento" value={form.metodo_pagamento} onChange={handleChange} />
                <input name="comprovativo_url" placeholder="Comprovativo URL" value={form.comprovativo_url} onChange={handleChange} />
                <input name="responsavel_id" placeholder="Responsável ID" value={form.responsavel_id} onChange={handleChange} />
                <button type="submit">Cadastrar</button>
            </form>
            {msg && <p>{msg}</p>}
            <ul>
                {despesas.map(d => (
                    <li key={d.id}>{d.categoria} - {d.valor} {d.moeda} ({d.estado})</li>
                ))}
            </ul>
        </div>
    );
}
export default Despesas;
