import React, { useEffect, useState } from 'react';
import axios from './api';

function GestaoFinanceira() {
    const [stats, setStats] = useState({ receitas: 0, despesas: 0, projetos: [] });
    const [extrato, setExtrato] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchFinanceData = async () => {
            try {
                const [rRec, rDes, rProj] = await Promise.all([
                    axios.get('/api/receita'),
                    axios.get('/api/despesa'),
                    axios.get('/api/projeto')
                ]);

                // Agregar por projeto
                const projData = rProj.data.map(p => {
                    const rec = rRec.data.filter(r => r.projeto_id === p.id).reduce((acc, curr) => acc + Number(curr.valor), 0);
                    const des = rDes.data.filter(d => d.projeto_id === p.id).reduce((acc, curr) => acc + Number(curr.valor), 0);
                    return { ...p, totalRec: rec, totalDes: des, saldo: rec - des };
                });

                // Criar extrato unificado
                const recs = rRec.data.map(r => ({ ...r, tipo: 'RECEITA', label: r.tipo_fundo || 'Receita' }));
                const dess = rDes.data.map(d => ({ ...d, tipo: 'DESPESA', label: d.fornecedor || 'Despesa' }));
                const unificado = [...recs, ...dess].sort((a, b) => new Date(b.data) - new Date(a.data));

                setStats({
                    receitas: rRec.data.reduce((acc, r) => acc + Number(r.valor), 0),
                    despesas: rDes.data.reduce((acc, d) => acc + Number(d.valor), 0),
                    projetos: projData
                });
                setExtrato(unificado);
            } catch (err) {
                console.error(err);
            } finally {
                setLoading(false);
            }
        };
        fetchFinanceData();
    }, []);

    if (loading) return <p>A carregar extratos financeiros detalhados...</p>;

    return (
        <div style={{ display: 'flex', flexDirection: 'column', gap: '2rem' }}>
            {/* Resumo Global */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '1.5rem' }}>
                <div className="card" style={{ borderLeft: '5px solid var(--accent)' }}>
                    <h4 style={{ color: 'var(--text-muted)', marginBottom: '0.5rem' }}>Fluxo Total de Entradas</h4>
                    <span style={{ fontSize: '2rem', fontWeight: '800', color: 'var(--accent)' }}>{stats.receitas.toLocaleString()} MT</span>
                </div>
                <div className="card" style={{ borderLeft: '5px solid var(--danger)' }}>
                    <h4 style={{ color: 'var(--text-muted)', marginBottom: '0.5rem' }}>Execução Financeira (Ganhos)</h4>
                    <span style={{ fontSize: '2rem', fontWeight: '800', color: 'var(--danger)' }}>{stats.despesas.toLocaleString()} MT</span>
                </div>
            </div>

            {/* Saldos por Projeto */}
            <div className="card">
                <h3 style={{ marginBottom: '1.5rem' }}>Saldos e Disponibilidade por Projeto</h3>
                <div style={{ overflowX: 'auto' }}>
                    <table style={{ width: '100%', borderCollapse: 'collapse', textAlign: 'left' }}>
                        <thead>
                            <tr style={{ color: 'var(--text-muted)', borderBottom: '1px solid var(--border)' }}>
                                <th style={{ padding: '1rem' }}>Projeto</th>
                                <th style={{ padding: '1rem' }}>Receitas</th>
                                <th style={{ padding: '1rem' }}>Despesas</th>
                                <th style={{ padding: '1rem' }}>Saldo Atual</th>
                                <th style={{ padding: '1rem' }}>Execução</th>
                            </tr>
                        </thead>
                        <tbody>
                            {stats.projetos.map(p => (
                                <tr key={p.id} style={{ borderBottom: '1px solid var(--border)' }}>
                                    <td style={{ padding: '1rem' }}>
                                        <strong>{p.nome}</strong><br />
                                        <small style={{ color: 'var(--text-muted)' }}>{p.codigo_interno}</small>
                                    </td>
                                    <td style={{ padding: '1rem', color: 'var(--accent)' }}>{p.totalRec.toLocaleString()} MT</td>
                                    <td style={{ padding: '1rem', color: 'var(--danger)' }}>{p.totalDes.toLocaleString()} MT</td>
                                    <td style={{ padding: '1rem', fontWeight: 'bold' }}>{p.saldo.toLocaleString()} MT</td>
                                    <td style={{ padding: '1rem' }}>
                                        <div style={{ width: '100px', height: '8px', background: '#334155', borderRadius: '4px', overflow: 'hidden' }}>
                                            <div style={{ width: `${Math.min((p.totalDes / p.totalRec) * 100, 100) || 0}%`, height: '100%', background: 'var(--primary)' }}></div>
                                        </div>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>

            {/* Extrato Detalhado */}
            <div className="card">
                <h3 style={{ marginBottom: '1.5rem' }}>Extrato de Movimentações Recentes</h3>
                <ul>
                    {extrato.map((item, idx) => (
                        <li key={idx} style={{ borderLeft: `4px solid ${item.tipo === 'RECEITA' ? 'var(--accent)' : 'var(--danger)'}`, marginBottom: '0.5rem' }}>
                            <div>
                                <strong style={{ color: item.tipo === 'RECEITA' ? 'var(--accent)' : 'var(--text-main)' }}>
                                    {item.tipo === 'RECEITA' ? '↑' : '↓'} {item.label}
                                </strong>
                                <br />
                                <small style={{ color: 'var(--text-muted)' }}>{item.data || 'Data N/D'}</small>
                            </div>
                            <div style={{ fontWeight: 'bold' }}>
                                {item.tipo === 'RECEITA' ? '+' : '-'} {Number(item.valor).toLocaleString()} MT
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default GestaoFinanceira;
