import React, { useState } from 'react';

import Dashboard from './Dashboard';
import Projetos from './Projetos';
import Usuarios from './Usuarios';
import Despesas from './Despesas';
import Receitas from './Receitas';
import Auditoria from './Auditoria';
import Login from './Login';

function App() {
    const [token, setToken] = useState(localStorage.getItem('token'));
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));

    const handleLogin = (jwt, userData) => {
        setToken(jwt);
        setUser(userData);
        localStorage.setItem('token', jwt);
        localStorage.setItem('user', JSON.stringify(userData));
    };

    const handleLogout = () => {
        setToken(null);
        setUser(null);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
    };

    const [modulo, setModulo] = useState('dashboard');

    if (!token) {
        return <Login onLogin={handleLogin} />;
    }

    return (
        <div>
            <h1>ERP Social para ONGs</h1>
            <button onClick={handleLogout}>Sair</button>
            <p>Bem-vindo, {user ? user.nome : 'usuário'}!</p>
            <nav>
                <button onClick={() => setModulo('dashboard')}>Dashboard</button>
                <button onClick={() => setModulo('projetos')}>Projetos</button>
                <button onClick={() => setModulo('usuarios')}>Usuários</button>
                <button onClick={() => setModulo('receitas')}>Receitas</button>
                <button onClick={() => setModulo('despesas')}>Despesas</button>
                <button onClick={() => setModulo('auditoria')}>Auditoria</button>
            </nav>
            {modulo === 'dashboard' && <Dashboard />}
            {modulo === 'projetos' && <Projetos />}
            {modulo === 'usuarios' && <Usuarios />}
            {modulo === 'receitas' && <Receitas />}
            {modulo === 'despesas' && <Despesas />}
            {modulo === 'auditoria' && <Auditoria />}
        </div>
    );
}

export default App;
