import React, { useEffect, useState } from 'react';
import axios from './api';

function Dashboard() {
    const [stats, setStats] = useState({ receitas: 0, despesas: 0, saldo: 0, execucao: 0, numAtivos: 0 });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchStats = async () => {
            try {
                const [rRec, rDes, rPat] = await Promise.all([
                    axios.get('/api/receita'),
                    axios.get('/api/despesa'),
                    axios.get('/api/patrimonio')
                ]);
                const totalRec = rRec.data.reduce((acc, r) => acc + Number(r.valor), 0);
                const totalDes = rDes.data.reduce((acc, d) => acc + Number(d.valor), 0);
                setStats({
                    receitas: totalRec,
                    despesas: totalDes,
                    saldo: totalRec - totalDes,
                    execucao: totalRec > 0 ? (totalDes / totalRec) * 100 : 0,
                    numAtivos: rPat.data.length
                });
            } catch (err) {
                console.error(err);
            } finally {
                setLoading(false);
            }
        };
        fetchStats();
    }, []);

    if (loading) return <div className="card">A carregar o Centro de Comando Inteligente...</div>;

    return (
        <div style={{ display: 'flex', flexDirection: 'column', gap: '2rem' }}>
            {/* Header com Insights Rápidos */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(240px, 1fr))', gap: '1.5rem' }}>
                <div className="card" style={{ background: 'linear-gradient(135deg, rgba(99, 102, 241, 0.1), rgba(168, 85, 247, 0.1))', border: '1px solid var(--primary)' }}>
                    <h3 style={{ fontSize: '2.5rem', fontWeight: '800' }}>{stats.saldo.toLocaleString()} <small style={{ fontSize: '1rem' }}>MT</small></h3>
                    <p style={{ color: 'var(--text-muted)' }}>Disponibilidade Imediata (Cash-on-Hand)</p>
                </div>
                <div className="card">
                    <h3 style={{ fontSize: '1.5rem' }}>{stats.execucao.toFixed(1)}%</h3>
                    <p style={{ color: 'var(--text-muted)' }}>Execução Orçamental Global</p>
                    <div style={{ width: '100%', height: '6px', background: '#334155', borderRadius: '3px', marginTop: '1rem' }}>
                        <div style={{ width: `${Math.min(stats.execucao, 100)}%`, height: '100%', background: 'var(--accent)', borderRadius: '3px' }}></div>
                    </div>
                </div>
                <div className="card" style={{ borderLeft: '4px solid var(--primary)' }}>
                    <h3 style={{ fontSize: '1.5rem' }}>{stats.numAtivos}</h3>
                    <p style={{ color: 'var(--text-muted)' }}>Bens em Inventário (Património)</p>
                </div>
            </div>

            {/* Visualização de Performance (Numbers that Sing) */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))', gap: '2rem' }}>
                <div className="card">
                    <h3>Performance vs Transparência</h3>
                    <div style={{ display: 'flex', alignItems: 'flex-end', gap: '20px', height: '200px', padding: '20px 0', borderBottom: '1px solid var(--border)' }}>
                        <div style={{ flex: 1, background: 'var(--accent)', height: '100%', borderRadius: '4px 4px 0 0', position: 'relative', minHeight: '20px' }}>
                            <span style={{ position: 'absolute', top: '-25px', width: '100%', textAlign: 'center', fontSize: '0.7rem' }}>Receitas</span>
                        </div>
                        <div style={{ flex: 1, background: 'var(--danger)', height: `${Math.max(stats.execucao, 5)}%`, borderRadius: '4px 4px 0 0', position: 'relative' }}>
                            <span style={{ position: 'absolute', top: '-25px', width: '100%', textAlign: 'center', fontSize: '0.7rem' }}>Despesas</span>
                        </div>
                    </div>
                    <p style={{ marginTop: '1rem', fontSize: '0.9rem', color: 'var(--text-muted)' }}>
                        O rácio de execução é de <strong>{stats.execucao.toFixed(2)}%</strong>. Monitorize para garantir que não ultrapassa o orçamento do doador.
                    </p>
                </div>

                <div className="card" style={{ borderLeft: '4px solid var(--accent)', background: 'rgba(45, 212, 191, 0.05)' }}>
                    <h3>Prontidão para Auditoria (Audit-Ready)</h3>
                    <ul style={{ marginTop: '1.5rem' }}>
                        <li style={{ background: 'transparent', border: 'none', padding: '0.5rem 0', display: 'flex', alignItems: 'center', gap: '10px' }}>
                            <span style={{ color: 'var(--accent)' }}>●</span> Todos os projetos com NUIT validado
                        </li>
                        <li style={{ background: 'transparent', border: 'none', padding: '0.5rem 0', display: 'flex', alignItems: 'center', gap: '10px' }}>
                            <span style={{ color: 'var(--accent)' }}>●</span> Folha de pagamento com INSS provisionado
                        </li>
                        <li style={{ background: 'transparent', border: 'none', padding: '0.5rem 0', display: 'flex', alignItems: 'center', gap: '10px' }}>
                            <span style={{ color: 'var(--accent)' }}>●</span> Patrimonio com códigos únicos ativos
                        </li>
                        <li style={{ background: 'transparent', border: 'none', padding: '0.5rem 0', display: 'flex', alignItems: 'center', gap: '10px' }}>
                            <span style={{ color: 'var(--danger)' }}>●</span> 1 comprovativo de despesa em falta
                        </li>
                    </ul>
                    <button className="nav-button" style={{ marginTop: '1rem', width: '100%', background: 'var(--primary)', color: 'white' }} onClick={() => alert('Dossiê de Inspecção gerado com sucesso para submissão.')}>
                        Gerar Dossiê de Inspecção
                    </button>
                    <small style={{ display: 'block', marginTop: '1rem', color: 'var(--text-muted)', textAlign: 'center' }}>
                        Pack completo: Balancetes, Folhas de Salário e Ativos.
                    </small>
                </div>
            </div>
        </div>
    );
}

export default Dashboard;
